// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "./AccountManagement.sol";
import "./CarbonToken.sol";
import "./CarbonBase.sol";

library Utils {
    function updateTotalCreditsAfterBuy(
        AccountManagement account,
        address buyer,
        address seller,
        uint256 amount
    ) public {
        account.setAccountTotalCredits(
            buyer,
            account.getAccountTotalCredits(buyer) + amount
        );
        account.setAccountTotalCredits(
            seller,
            account.getAccountTotalCredits(seller) - amount
        );
    }

    function updateCreditsAfterRetire(
        AccountManagement account,
        address accountAddress,
        uint256 amount
    ) public {
        account.setAccountTotalCredits(
            accountAddress,
            account.getAccountTotalCredits(accountAddress) - amount
        );
        account.setAccountTotalRetire(
            accountAddress,
            account.getAccountTotalRetire(accountAddress) + amount
        );
    }
}

contract CarbonMarket is CarbonBase {
    CarbonToken private _token;
    AccountManagement private _account;
    uint256 private _listedTokens; // number of listed credits
    uint256 private _tokenIds;

    constructor(address _tokenAddress, address _accountAddress) {
        _token = CarbonToken(_tokenAddress);
        _account = AccountManagement(_accountAddress);
    }

    modifier onlyAuditor() {
        require(_account.isAuditor(msg.sender), "IAu");
        _;
    }

    function getListedTokensCount() public view returns (uint256) {
        // returns the total number of listed credits
        return _listedTokens;
    }

    function registerProject(
        uint256 tokenSupply,
        string memory tokenURI,
        uint256 price
    ) public {
        require(
            tokenSupply <=
                _account.getAccountTotalCredits(msg.sender),
            "ITS"
        );
        require(price > 0, "Invalid price");
        uint256 currentId = ++_tokenIds; // tokenID starts from 1
        _token.setCarbonCredit(
            currentId,
            msg.sender,
            ApprovalStatus.Pending,
            price,
            false
        );
        _token.setTokenSupply(currentId, tokenSupply);
        _token.setURI(currentId, tokenURI);
        _token.getAddTokenToAllTokensEnumeration(currentId);
    }

    function approveProject(uint256 tokenId) public payable onlyAuditor {
        CarbonCredit memory credit = _token.getCarbonCredit(tokenId);

        require(credit.status == ApprovalStatus.Pending, "IS");

        _token.updateStatus(tokenId, ApprovalStatus.Approved);
        _token.mint(
            credit.initialOwner,
            tokenId,
            _token.getTokenSupply(tokenId),
            ""
        );

        emit CarbonCreditCreated(
            tokenId,
            msg.sender,
            _token.getTokenSupply(tokenId),
            block.timestamp
        );
        emit CarbonCreditAudited(
            tokenId,
            msg.sender,
            credit.initialOwner,
            ApprovalStatus.Approved,
            block.timestamp
        );

        listCreditsForSale(tokenId, credit.pricePerCredit);
    }

    function declineProject(uint256 tokenId) public onlyAuditor {
        CarbonCredit memory credit = _token.getCarbonCredit(tokenId);

        require(credit.status == ApprovalStatus.Pending, "IS");
        _token.updateStatus(tokenId, ApprovalStatus.Declined);
        emit CarbonCreditAudited(
            tokenId,
            msg.sender,
            credit.initialOwner,
            ApprovalStatus.Declined,
            block.timestamp
        );
    }

    function listCreditsForSale(uint256 tokenId, uint256 price) public {
        CarbonCredit memory credit = _token.getCarbonCredit(tokenId);

        require(
            _token.balanceOf(credit.initialOwner, tokenId) > 0,
            "ITB"
        );
        require(credit.status == ApprovalStatus.Approved, "IS");
        require(!credit.isListed, "IIS.");
        require(price > 0, "IP");
        require(
            _token.isApprovedForAll(credit.initialOwner, address(this)),
            "MNA"
        );

        _token.updateListed(tokenId, true);
        _token.updatePrice(tokenId, price);
        _listedTokens += 1;

        emit CarbonCreditListed(
            tokenId,
            credit.initialOwner,
            _token.getTokenSupply(tokenId),
            price,
            block.timestamp
        );
    }

    function getAllListedCredits() public view returns (CarbonCredit[] memory) {
        // [{}, {}, ...]
        uint allCreditsCount = _token.getTotalTokensCount();
        uint currentIndex = 0;
        CarbonCredit[] memory credits = new CarbonCredit[](_listedTokens);

        for (uint i = 0; i < allCreditsCount; i++) {
            uint tokenId = _token.getTokenByIndex(i);
            CarbonCredit memory credit = _token.getCarbonCredit(tokenId);
            if (credit.isListed == true) {
                credits[currentIndex] = credit;
                currentIndex += 1;
            }
        }
        return credits;
    }

    function buyCredits(uint256 tokenId, uint256 amount) public payable {
        CarbonCredit memory credit = _token.getCarbonCredit(tokenId);

        require(credit.isListed, "IIS");
        require(
            _token.balanceOf(credit.initialOwner, tokenId) >= amount,
            "IB"
        );
        require(
            msg.value >= credit.pricePerCredit * amount,
            "IF"
        );
        require(
            _token.isApprovedForAll(credit.initialOwner, address(this)),
            "MNA."
        );

        _token.safeTransferFrom(
            credit.initialOwner,
            msg.sender,
            tokenId,
            amount,
            ""
        );
        payable(credit.initialOwner).transfer(msg.value);

        _token.updateTokenSold(tokenId, amount);

        // Additional custom logic post-transfer
        // If all tokens of this ID are transferred, update the CarbonCredit struct
        if (_token.balanceOf(credit.initialOwner, tokenId) == 0) {
            _token.updateListed(tokenId, false);
            _listedTokens--;
        }

        // _account.setAccountTotalCredits(credit.initialOwner, _account.getAccountTotalCredits(credit.initialOwner) - amount);
        // _account.setAccountTotalCredits(msg.sender, _account.getAccountTotalCredits(msg.sender) + amount);

        Utils.updateTotalCreditsAfterBuy(_account, msg.sender, credit.initialOwner, amount);

        emit CarbonCreditPurchased(
            tokenId,
            credit.initialOwner,
            msg.sender,
            amount,
            block.timestamp
        );
    }

    function retireCredits(uint256 tokenId, uint256 amount) public {
        require(amount > 0, "IAm");
        require(
            _token.balanceOf(msg.sender, tokenId) >= amount,
            "IB"
        );
        require(
            _token.isApprovedForAll(msg.sender, address(this)),
            "MNA"
        );

        _token.burn(msg.sender, tokenId, amount);

        // _account.setAccountTotalCredits(msg.sender, _account.getAccountTotalCredits(msg.sender) - amount);
        // _account.setAccountTotalRetire(msg.sender, _account.getAccountTotalRetire(msg.sender) + amount);

        Utils.updateCreditsAfterRetire(_account, msg.sender, amount);

        emit CarbonCreditRetired(tokenId, msg.sender, amount, block.timestamp);
    }
}
